//å·¦å­æ ‘æ ¹ç»“ç‚¹åœ°å€ struct BiTNode *rchild; //å³å­æ ‘æ ¹ç»“ç‚¹åœ°å€ }BiTNode, *BiTree; Operations //åˆå§‹åŒ–äºŒå‰æ ‘root void InitBiTree(BiTree &amp;root){ root = NULL;//é€šè¿‡ç½®ç©ºæ¥æ¸…æ´—ä¹‹é—´rootæ‰€åœ¨ç©ºé—´ä¸­åœ°å€ root = (BiTree)malloc(sizeof(BiTNode)); root -&gt; data = 1; root -&gt; lchild = NULL; root -&gt; rchild = NULL; } //å‘äºŒå‰æ ‘rootä¸­æ’å…¥å€¼ä¸º val çš„ç»“ç‚¹ bool InsertBiTNode(BiTree &amp;root, ElemType val){ BiTNode *new_node = (BiTNode *)malloc(sizeof(BiTNode)); if(new_node == NULL){ return false; } if(root -&gt; lchild == NULL){ root -&gt; lchild = new_node; new_node -&gt; data = val; new_node -&gt; lchild = NULL; new_node -&gt; rchild = NULL; } else if(root -&gt; rchild == NULL){ root -&gt; rchild = new_node; new_node -&gt; data = val; new_node -&gt; lchild = NULL; new_node -&gt; rchild = NULL; } else{ std::cout &lt;&lt; &#34;Invalid root!&#34; &lt;&lt; std::endl; return false; } } //å®šä¹‰æ–¹ä½äºŒå‰æ ‘ç»“ç‚¹çš„è¡Œä¸º void observe(BiTree T){ std::cout &lt;&lt; T -&gt; data &lt;&lt; std::endl; } //å‰æ ¹éå†äºŒå‰æ ‘ T //ä½¿ç”¨é€’å½’å®šä¹‰ void PreOrder(BiTree T){ if(T != NULL){ observe(T); PreOrder(T -&gt; lchild); PreOrder(T -&gt; rchild); } } //ä¸­æ ¹éå†äºŒå‰æ ‘ T //ä½¿ç”¨é€’å½’å®šä¹‰ void InOrder(BiTree T){ if(T != NULL){ InOrder(T -&gt; lchild); observe(T); InOrder(T -&gt; rchild); } } //åæ ¹éå†äºŒå‰æ ‘ T //ä½¿ç”¨é€’å½’å®šä¹‰ void PostOrder(BiTree T){ if(T != NULL){ PostOrder(T -&gt; lchild); PostOrder(T -&gt; rchild); observer(T); } } //å±‚æ¬¡éå†äºŒå‰æ ‘ T //å€ŸåŠ©é˜Ÿåˆ—æ¨¡æ¿ç±»å®ç° void LevelOrder(BiTree T){ Queue&lt;BiTree,20&gt;queue; BiTree p = NULL; queue.Enqueue(T); while(!queue.isEmpty()){ queue.DeQueue(p); observe(p); if(p -&gt; lchild != NULL){ queue.EnQueue(p -&gt; lchild); } if(p -&gt; rchild != NULL){ queue.EnQueue(p -&gt; rchild); } } } Thread Tree Definition typedef struct ThreadNode{ ElemType data; //æ•°æ®åŸŸ struct ThreadNode *lchild; //å·¦å­æ ‘çš„æ ¹ç»“ç‚¹åœ°å€ struct ThreadNode *rchild; //å³å­æ ‘çš„æ ¹ç»“ç‚¹åœ°å€ int ltag; //æ ‡è®°å·¦å­æ ‘æ ¹ç»“ç‚¹æ˜¯å¦ä¸ºçº¿ç´¢, 1 ä¸º æ˜¯, 0 ä¸º é int rtag; //æ ‡è®°å³å­æ ‘æ ¹ç»“ç‚¹æ˜¯å¦ä¸ºçº¿ç´¢, 1 ä¸º æ˜¯, 0 ä¸º é } Operation //ä½¿ç”¨å…¨å±€å˜é‡ pre æ¥æŒ‡å®šå‰é©±ç»“ç‚¹ ThreadNode *pre = NULL; //è®¿é—®è¡Œä¸º visit() void visit(ThreadNode *nodeptr){ //æ²¡æœ‰å·¦å­©å­, åˆ™ç©ºé“¾åŸŸå¯ä»¥ç”¨æ¥å­˜å‚¨å‰é©±ç»“ç‚¹åœ°å€ if(nodeptr -&gt; lchild == NULL){ nodeptr -&gt; lchild = pre; nodeptr -&gt; ltag = 1; } //æ²¡æœ‰å³å­©å­, åˆ™ç©ºé“¾åŸŸå¯ä»¥ç”¨æ¥å­˜å‚¨åç»§ç»“ç‚¹åœ°å€ if(pre != NULL &amp;&amp; pre -&gt; rchild == NULL){ pre -&gt; rchild = nodeptr; pre -&gt; rtag = 1; } pre = nodeptr; }
